    // 1. Seleccionar los elementos del DOM
const botonModoOscuro = document.getElementById('modo-oscuro-btn');
const body = document.querySelector('body');

// 2. Escuchar el evento 'click' en el botón
botonModoOscuro.addEventListener('click', () => {
  // 3. Cuando se hace clic, añade o quita la clase 'dark-mode' del body
  body.classList.toggle('dark-mode');
});

// Seleccionamos el contenedor donde irán los datos
const userListContainer = document.getElementById('user-list');

// Usamos fetch para pedir los datos a la API
fetch('https://jsonplaceholder.typicode.com/users')
  .then(response => response.json()) // Convertimos la respuesta a JSON
  .then(users => {
    // Creamos el título dentro del contenedor
    userListContainer.innerHTML = '<h3>Usuarios de Prueba</h3>';

    // Mostramos solo los primeros 5 usuarios
    users.slice(0, 5).forEach(user => {
      const userElement = document.createElement('p');
      userElement.textContent = user.name;
      userListContainer.appendChild(userElement);
    });
  })
  .catch(error => console.error('¡Hubo un error al cargar los datos!', error));



